import react, { useEffect } from "react";
import LiveChat from "./LiveChat";
import { useDispatch, useSelector } from "react-redux";
import { addMessage } from "../utils/chatSlice";
import { generateRandomNames, generateString } from "../utils/helper";

const ChatMessages = ()=>{

const dispatch = useDispatch();

const chats = useSelector(store=>store.chat.messages);

useEffect(()=>{
  const i =  setInterval(()=>{ console.log("API Polling")
    dispatch(addMessage({
        name:generateRandomNames(),
        message:`${generateString(OFF_)} ðŸŽ¯`,
    }))
  } , 500);

  return ()=> clearInterval(i);

} , []);




    return (
        <div className="border border-black ml-2 w-96  h-[500px] overflow-y-scroll scroll flex flex-col-reverse ">
            {
                chats.map((c , i)=>{
                    return(
                        <LiveChat name={c.name} message={c.message}/>
                    )
                })
            }
        </div>
    )
}
export default ChatMessages;
